// Fun├º├úo para converter data do formato brasileiro
function converterData(dataString) {
  if (!dataString || dataString === '') return null;

  try {
    // Formato: DD/MM/YYYY
    const partes = dataString.split('/');
    if (partes.length === 3) {
      const dia = parseInt(partes[0]);
      const mes = parseInt(partes[1]) - 1; // M├¬s ├® 0-indexed
      const ano = parseInt(partes[2]);

      const data = new Date(ano, mes, dia);
      // Verificar se a data ├® v├ílida
      if (!isNaN(data.getTime())) {
        return data.toISOString();
      }
    }
    return null;
  } catch (error) {
    console.log('Erro ao converter data:', dataString, error);
    return null;
  }
}

