function formatarDataBR(dataString) {
  if (!dataString || dataString === '') return 'N├úo definida';

  try {
    // Tenta parsear datas no formato DD/MM/YYYY
    const [day, month, year] = dataString.split('/');
    if (day && month && year) {
      const date = new Date(year, month - 1, day);
      return format(date, 'dd/MM/yyyy');
    }
    return dataString;
  } catch (error) {
    return dataString;
  }
}

