function cleanAndSelectArticles(articlesWithContent) {
  const processedArticles = articlesWithContent.map(article => {
    // Remove conte├║do irrelevante (Hacker News sem conte├║do)
    if (article.fullContent === "Conte├║do n├úo dispon├¡vel" || !article.fullContent) {
      return null;
    }

    // Limpa o conte├║do HTML e excesso de espa├ºos
    let cleanContent = article.fullContent
      .replace(/<[^>]*>/g, '') // Remove tags HTML
      .replace(/\s+/g, ' ') // Normaliza espa├ºos
      .replace(/\[.*?\]/g, '') // Remove [chars]
      .trim();

    // Extrai os pontos principais (primeiros 1000 caracteres geralmente t├¬m o n├║cleo)
    const mainContent = cleanContent.substring(0, 1500);

    // Calcula score de qualidade baseado em:
    // - Tamanho do conte├║do
    // - Presen├ºa de palavras-chave relevantes
    // - Fonte confi├ível
    const qualityScore = calculateQualityScore(article, cleanContent);

    return {
      title: article.title,
      source: article.source.name,
      url: article.url,
      cleanContent: mainContent,
      qualityScore: qualityScore,
      hasImage: !!article.urlToImage,
      topic: categorizeTopic(article.title, mainContent)
    };
  }).filter(article => article !== null && article.qualityScore > 5); // Filtra por qualidade

  // Ordena por score e seleciona o melhor
  return processedArticles.sort((a, b) => b.qualityScore - a.qualityScore)[0];
}

function calculateQualityScore(article, content) {
  let score = 0;

  // Score por tamanho do conte├║do
  if (content.length > 500) score += 3;
  if (content.length > 1000) score += 2;

  // Score por palavras-chave relevantes
  const techKeywords = ['AI', 'artificial intelligence', 'machine learning', 'software', 'development', 'tech', 'innovation', 'digital', 'startup'];
  const matches = techKeywords.filter(keyword => 
    content.toLowerCase().includes(keyword.toLowerCase())
  ).length;
  score += matches;

  // Score por fonte confi├ível
  const trustedSources = ['TechRadar', 'CNBC', 'Ars Technica', 'The Verge'];
  if (trustedSources.includes(article.source.name)) score += 2;

  return score;
}

function categorizeTopic(title, content) {
  const text = (title + ' ' + content).toLowerCase();

  if (text.includes('ai') || text.includes('artificial intelligence') || text.includes('gemini')) {
    return 'AI';
  } else if (text.includes('apple') || text.includes('iphone') || text.includes('watch')) {
    return 'Apple';
  } else if (text.includes('google') || text.includes('android')) {
    return 'Google';
  } else if (text.includes('game') || text.includes('gaming') || text.includes('playtest')) {
    return 'Gaming';
  } else {
    return 'Technology';
  }
}

